import React from "react";
import ReactInputMask from "react-input-mask";




export default function DocNumber({ value, onChange, ...props }) {
  let [mask, setMask] = React.useState(null);

  const handleChange = ev => {
    let { value } = ev.target;
    let nums = value.replace(/[^0-9]/g, "");
    if (value.length === 3) {
      setMask(value[2] === "." ? "99.999.999/9999-99" : "999.999.999-99");
    }
    if (nums.length < 3 && mask != null) {
      setMask(null);
      ev.target.value = nums;
    }
    onChange(ev);
  };
  return (
      <ReactInputMask
        mask={mask}
        onChange={handleChange}
        maskChar="_"
        value={value}
        {...props}
      />
  );
}
